import { Component, inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { FormsModule } from '@angular/forms';
import { NewRecordComponent } from "./new-record/new-record.component";


@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, FormsModule, NewRecordComponent],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  title = 'API Client';
  http = inject(HttpClient);
  emps: any;
  emp: any
  id: number | undefined;
  status: number | undefined;

  displayall = false;
  findone = false;
  displayone = false;
  isAddingRecord = false;

  ngOnInit(): void {}

  displayAll() {
    this.http.get('https://localhost:5001/api/Employee').subscribe({
      next: response => this.emps = response,
      error: error => console.log(error),
      complete: () => console.log("Request completed.")
    });
    this.displayall = true;
    this.findone = false;
    this.displayone = false;
  }
  displayClose() {
    this.displayall = false;
  }

  findOne() {
    this.findone = true;
    this.displayall = false;
  }
  onSubmit() {
    var empId = this.id;
    var request = "https://localhost:5001/api/Employee/"+empId?.toString()
    this.http.get(request).subscribe({
      next: response => {this.emp = response; this.status = 1; },
      error: error => { console.log(error); this.status = 0; },
      complete: () => console.log("Request completed.")
    });
    this.displayone = true;
  }
  findClose() {
    this.findone = false;
    this.displayone = false;
    this.id = 0;
  }

  onStartAddTask() {
    this.isAddingRecord = true;
  }

  onCancel() {
    this.isAddingRecord = false;
  }

}
