import { Component, inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet } from '@angular/router';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { FormsModule } from '@angular/forms';
import { NewRecordComponent } from "./new-record/new-record.component";
import { Record } from './new-record/new-record.component';
import { HttpErrorResponse } from '@angular/common/http';

interface employee {
  empId: number;
  empName: string;
}

interface ApiResponse {
  data: employee | employee[] | string;
  status: number
}

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, FormsModule, NewRecordComponent],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor(private http: HttpClient) {} // strong type safety

  title = 'API Client';
  // http = inject(HttpClient); // constructor, avoid any

  emplist: employee[] = [];
  emp: employee | undefined;
  id: number | undefined;
  status: number | undefined;

  displayall = false;
  findone = false;
  displayone = false;
  deleteone = false;
  isAddingRecord = false;
  afterSubmit = false;
  displaydelete = false;

  submitStatus = '';


  displayAll() {    
    this.http.get<ApiResponse>('https://localhost:5001/api/Employees').subscribe(
      response  => {
        this.status = response.status;
        if(this.status == 1){
          this.emplist = response.data as employee[]
        } else {
          this.submitStatus = response.data as string
        }
      }
    );
    this.displayall = true;
    this.findone = false;
    this.displayone = false;
    this.isAddingRecord = false;
    this.afterSubmit = false;
    this.deleteone = false;
    this.displaydelete = false;
  }
  displayClose() {
    this.displayall = false;
  }


  findOne() {
    this.displayall = false;
    this.findone = true;
    this.displayone = false;
    this.isAddingRecord = false;
    this.afterSubmit = false;
    this.deleteone = false;
    this.displaydelete = false;
  }
  onSubmit() {
    var empId = this.id;
    if(empId != undefined)
    {
      var request = "https://localhost:5001/api/Employees/"+empId?.toString()
      this.http.get<ApiResponse>(request).subscribe(

        response => {
          this.status = response.status;
          if(this.status == 1) {
            this.emp = response.data as employee;
          } else {
            this.submitStatus = response.data as string;
          }
        });
    } 
    else 
    {
      this.status = -1;
      this.submitStatus = "Please enter an Emp Id.";
    }
    this.displayone = true;
    this.id = undefined;
  }
  findClose() {
    this.findone = false;
    this.displayone = false;
    this.id = 0;
  }


  onStartAddRec() {
    this.isAddingRecord = true;
    this.displayall = false;
    this.findone = false;
    this.displayone = false;
    this.afterSubmit = false;
    this.deleteone = false;
    this.displaydelete = false;
  }
  onCancel() {
    this.isAddingRecord = false;
  }
  onAddRecord(record: Record) {
    this.isAddingRecord = false;
    console.log(record);

    const url = 'https://localhost:5001/api/Employees';
    const options = { headers: new HttpHeaders().set('Content-Type', 'application/json') };

    this.http.post<ApiResponse>(url, record, options).subscribe(
      response => {
        this.status = response.status;
        if(this.status == 1) {
          this.submitStatus = response.data as string;
        } else {
          this.submitStatus = response.data as string;
        }
      }
    );

    this.afterSubmit = true;
    this.id = undefined
    console.log("Submit status:",this.submitStatus);

  }
  closeStatus() {
    this.afterSubmit = false;
  }

  onStartDelRec() {
    this.deleteone = true;
    this.isAddingRecord = false;
    this.displayall = false;
    this.findone = false;
    this.displayone = false;
    this.afterSubmit = false;    
    this.displaydelete = false;
  }
  deleteRec() {
    var empId = this.id;
    if(empId != null) {
      var request = "https://localhost:5001/api/Employees/"+empId?.toString()
      this.http.delete<ApiResponse>(request).subscribe(
        response => {
          this.status = response.status;
          this.submitStatus = response.data as string;
        }
      );
    }
    else {
      this.submitStatus = "Emp Id cannot be empty."
      this.status = -1
    }
    this.displaydelete = true;
    this.id = undefined
  }

  closeDelete() {
    this.deleteone = false;
    this.displaydelete = false;
  }

}
<div *ngIf="isAddingRecord">
    <app-new-record (close)="onCancel()" (entry)="onAddRecord($event)"></app-new-record>
</div>

<div class="head">
    <h1>Employees</h1>
    <button id='main' (click)="displayAll()"> Display All Employees </button>
    <button id='main' (click)="findOne()"> Search Employee by EmpId </button>
    <button id='main' (click)="onStartAddRec()"> Add Employee Record </button>
    <button id='main' (click)="onStartDelRec()"> Delete Employee Record </button>
    <br>
</div>

<div class="row" id="allrow">
    <div class="column" id="all" *ngIf="displayall">
        <div *ngIf="status == 1">
            <ul>
                <li *ngFor="let emp of emplist">
                    Id: {{ emp.empId }}
                    Name: {{ emp.empName }} <br>
                </li>
            </ul>
            <button (click)="displayClose()"> Close </button>
        </div>
    
        <div *ngIf="status != 1 ">

                {{ submitStatus }}
        </div>
    </div>
</div>

<div class="row" id="allrow">    
    <div id='sub' class="column" id="one" *ngIf="findone">
        <form (ngSubmit)="onSubmit()">
            <br>
            Emp Id to find:
            <input type="text" id="id" name="id" [(ngModel)]="id" autocomplete="off" />
            <br><br>
            <button type="submit">Search</button><button (click)="findClose()"> Close </button>
        </form><br>
    </div>
    
    <div id='sub' class="column" id="one" *ngIf="displayone">
        <div *ngIf="status == 1">
            Record found:
            <ul>
                <li>Id: {{emp?.empId}}<br> Name: {{emp?.empName}}</li>
            </ul>
        </div>
        <div *ngIf="status != 1">
            {{ submitStatus }}
        </div>
    </div>
</div>

<div class="row" id="allrow">    
    <div id='sub' class="column" id="one" *ngIf="deleteone">
        <form (ngSubmit)="deleteRec()">
            <br>
            Emp Id to delete:
            <input type="text" id="id" name="id" [(ngModel)]="id" autocomplete="off" />
            <br><br>
            <button type="submit">Delete</button><button (click)="closeDelete()"> Close </button>
        </form><br>
    </div>
    
    <div id='sub' class="column" id="one" *ngIf="displaydelete">
        {{ submitStatus }}
    </div>
</div>

<div *ngIf="afterSubmit" class="row" id="allrow">
    <div class="column" id="one">
        <p> {{ submitStatus }} </p>
        <button (click)="closeStatus()"> Close </button>
    </div>
</div>
