import { Component, inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { FormsModule } from '@angular/forms';


@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, FormsModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  title = 'API Client';
  http = inject(HttpClient);
  emps: any;
  emp: any
  id = 0;
  status = 0;

  displayall = false;
  findone = false;
  displayone = false;

  ngOnInit(): void {}

  displayAll() {
    this.http.get('https://localhost:5001/api/Employee').subscribe({
      next: response => this.emps = response,
      error: error => console.log(error),
      complete: () => console.log("Request completed.")
    });
    this.displayall = true;
  }
  displayClose() {
    this.displayall = false;
  }

  findOne() {
    this.findone = true;
  }
  onSubmit() {
    var empId = this.id;
    var request = "https://localhost:5001/api/Employee/"+empId.toString()
    this.http.get(request).subscribe({
      next: response => {this.emp = response; this.status = 1; },
      error: error => { console.log(error); this.status = 0; },
      complete: () => console.log("Request completed.")
    });
    this.displayone = true;
  }
  findClose() {
    this.findone = false;
  }

}
<div class="head">
    <h1>Employees</h1>
    <button id='main' (click)="displayAll()"> Display All Employees </button>
    <button id='main' (click)="findOne()"> Search Employee by EmpId </button>
    <br>
</div>

<div class="row" id="allrow">
    <div class="column" id="all" *ngIf="displayall">
        <ul>
            <li *ngFor="let emp of emps">
                ID: {{ emp.empId }} <br>
                Name: {{ emp.empName }} <br>
            </li>
        </ul>
        <button (click)="displayClose()"> Close </button>
    </div>
</div>

<div class="row">    
    <div id='sub' class="column" id="one" *ngIf="findone">
        <form (ngSubmit)="onSubmit()">
            <label for="title">Emp Id: </label>
            <input type="text" id="id" name="id" [(ngModel)]="id"/>
            <button type="submit">Search</button> 
        </form>
        <button (click)="findClose()"> Close </button>
    </div>
    
    <div id='sub' class="column" id="one" *ngIf="displayone">
        <div *ngIf="status == 0">
            No Such Employee Found
        </div>
        <div *ngIf="status == 1">
            Found:
            <ul>
                <li>Id: {{emp.empId}}<br> Name: {{emp.empName}}</li>
            </ul>
        </div>
    </div>

</div>
