import { Component, inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet } from '@angular/router';
import { HttpClient } from '@angular/common/http';


@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  title = 'API Client';
  http = inject(HttpClient);
  emps: any;
  id: any;

  displayall = false;
  findone = false;

  ngOnInit(): void {}

  displayAll() {
    this.http.get('https://localhost:5001/api/Employee').subscribe({
      next: response => this.emps = response,
      error: error => console.log(error),
      complete: () => console.log("Request completed.")
    });
    this.displayall = true;
  }
  displayClose() {
    this.displayall = false;
  }

  findOne() {
    this.findone = true;
  }
  onSubmit() {

  }
  findClose() {
    this.findone = false;
  }

}
<div>
    <h1>Employees</h1>
    <button (click)="displayAll()"> Display All Employees </button>
    <button (click)="findOne()"> Find Employee by EmpId </button>
</div>
<div *ngIf="displayall">
    <ul>
        <li *ngFor="let emp of emps">
            ID: {{ emp.empId }} <br>
            Name: {{ emp.empName }} <br>
        </li>
    </ul>
    <button (click)="displayClose()"> Close </button>
</div>
<div *ngIf="findone">
    <form (ngSubmit)="onSubmit()">

    </form>
    <button (click)="findClose()"> Close </button>
</div>
