import { Component, inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet } from '@angular/router';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { FormsModule } from '@angular/forms';
import { NewRecordComponent } from "./new-record/new-record.component";
import { Record } from './new-record/new-record.component';
import { HttpErrorResponse } from '@angular/common/http';

import { ValidatorFn, AbstractControl } from '@angular/forms';

export function requiredValidator(): ValidatorFn {
  return (control: AbstractControl): { [key: string]: any } | null => {
    return control.value ? null : { required: true };
  };
}

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, FormsModule, NewRecordComponent],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  title = 'API Client';
  http = inject(HttpClient); // constructor, avoid any
  emps: any;
  emp: any
  id: number | undefined;
  status: number | undefined;

  displayall = false;
  findone = false;
  displayone = false;
  isAddingRecord = false;
  afterSubmit = false;
  submitStatus = '';

  ngOnInit(): void {} // life cycle

  displayAll() {
    this.http.get('https://localhost:5001/api/Employee').subscribe({
      next: response => { this.emps = response; this.status = 1; },
      error: error => { 
        if (error instanceof HttpErrorResponse) 
        {
          this.status = 0;
          if(error.status == 0)
            this.submitStatus = 'Error: Cannot connect to the Database.';
          else 
            this.submitStatus = error.error;
        }
      },
      complete: () => { console.log("Request completed."); this.status = 1; }
    }); // observable
    this.displayall = true;
    this.findone = false;
    this.displayone = false;
    this.isAddingRecord = false;
    this.afterSubmit = false;
  }
  displayClose() {
    this.displayall = false;
  }

  findOne() {
    this.displayall = false;
    this.findone = true;
    this.displayone = false;
    this.isAddingRecord = false;
    this.afterSubmit = false;
  }
  onSubmit() {
    var empId = this.id;
    if(empId != undefined)
    {
      var request = "https://localhost:5001/api/Employee/"+empId.toString()
      this.http.get(request).subscribe({

        next: response => {this.emp = response; this.status = 1; },
        error: error => { 
          if (error instanceof HttpErrorResponse) 
          {
            this.status = 0; 
            if(error.status == 0)
              this.submitStatus = 'Error: Cannot connect to the Database.';
            else 
              this.submitStatus = error.error;
          }
        },
        complete: () => console.log("Request completed.")

      });
    } else {
      this.status = -1;
      this.submitStatus = "Please enter an Emp Id.";
    }
    this.displayone = true;
  }
  findClose() {
    this.findone = false;
    this.displayone = false;
    this.id = 0;
  }

  onStartAddTask() {
    this.isAddingRecord = true;
    this.displayall = false;
    this.findone = false;
    this.displayone = false;
    this.afterSubmit = false;
  }

  onCancel() {
    this.isAddingRecord = false;
  }

  onAddRecord(record: Record) {
    this.isAddingRecord = false;
    console.log(record);
    const url = 'https://localhost:5001/api/Employee';
    const options = { headers: new HttpHeaders().set('Content-Type', 'application/json') };

    this.submitStatus = '';

    this.http.post(url, record, options).subscribe({
      next: response => { 
        this.submitStatus = 'Record Inserted Succefully.';
        console.log(response); this.status = 1; },
      error: error => { 
        if (error instanceof HttpErrorResponse) 
        {
          if(error.status == 200)
          {
            this.submitStatus = 'Record Inserted Sucessfully.';
          }
          else if(error.status == 0)
          {
            this.submitStatus = 'Error: Cannot connect to the Database.';
          }
          else {
            this.submitStatus = error.error;
            console.log(error); this.status = 0; 
          }
        }
        else {
          this.submitStatus = 'Record Insertion Unsuccessful. Try Again.';
        }
      },
      complete: () => { 
        this.submitStatus = 'Record Inserted Succefully.';
        console.log("Request completed."); this.status = 1; }
    });

    this.afterSubmit = true;
    console.log("Submit status:",this.submitStatus);

  }

  closeStatus() {
    this.afterSubmit = false;
  }
}
<div *ngIf="isAddingRecord">
    <app-new-record (close)="onCancel()" (entry)="onAddRecord($event)"></app-new-record>
</div>

<div class="head">
    <h1>Employees</h1>
    <button id='main' (click)="displayAll()"> Display All Employees </button>
    <button id='main' (click)="findOne()"> Search Employee by EmpId </button>
    <button id='main' (click)="onStartAddTask()"> Add Employee Record </button>
    <br>
</div>

<div class="row" id="allrow">
    <div class="column" id="all" *ngIf="displayall">
        <div *ngIf="status == 1">
            <ul>
                <li *ngFor="let emp of emps">
                    Id: {{ emp.empId }}
                    Name: {{ emp.empName }} <br>
                </li>
            </ul>
            <button (click)="displayClose()"> Close </button>
        </div>
    
        <div *ngIf="status != 1 ">

                {{ submitStatus }}
        </div>
    </div>
</div>

<div class="row" id="allrow">    
    <div id='sub' class="column" id="one" *ngIf="findone">
        <form (ngSubmit)="onSubmit()">
            <br>
            Emp Id:
            <input type="text" id="required" name="id" [(ngModel)]="id" autocomplete="off" />
            <br><br>
            <button type="submit">Search</button><button (click)="findClose()"> Close </button>
        </form><br>
    </div>
    
    <div id='sub' class="column" id="one" *ngIf="displayone">
        <div *ngIf="status == 1">
            Record found:
            <ul>
                <li>Id: {{emp.empId}}<br> Name: {{emp.empName}}</li>
            </ul>
        </div>
        <div *ngIf="status != 1">
            {{ submitStatus }}
        </div>
    </div>
</div>

<div *ngIf="afterSubmit" class="row" id="allrow">
    <div class="column" id="one">
        <p> {{ submitStatus }} </p>
        <button (click)="closeStatus()"> Close </button>
    </div>
</div>
